defined('_JEXEC') or die;

$value = $field->value;

if ($value == '')
{
	return;
}

$attributes = '';

if (!JUri::isInternal($value))
{
	$attributes = ' rel="nofollow noopener noreferrer" target="_blank"';
}

/** Facebook URL **/
if ($field->id == 31){
echo sprintf('<a href="%s"%s> <img alt="Join us on Facebook" src="data:image/svg+xml;base64," style="width:32px;height:auto;"></a>',
	htmlspecialchars($value),
	$attributes,
	htmlspecialchars($value)
);
} 

/** Twitter URL **/
elseif ($field->id == 32){
echo sprintf('<a href="%s"%s> <img alt="Follow us on Twitter" src="data:image/svg+xml;base64," style="width:32px;height:auto;"></a>',
	htmlspecialchars($value),
	$attributes,
	htmlspecialchars($value)
);
}

/** Website URL **/
elseif ($field->id == 29){
echo sprintf('<a href="%s"%s> <img alt="Visit our Website" src="data:image/svg+xml;base64," style="width:32px;height:auto;"></a>',
	htmlspecialchars($value),
	$attributes,
	htmlspecialchars($value)
);
}
  
/** Meetup URL **/
elseif ($field->id == 30){
echo sprintf('<a href="%s"%s> <img alt="View our Meetups" src="data:image/svg+xml;utf8;base64," style="width:32px;height:auto;"></a>',
	htmlspecialchars($value),
	$attributes,
	htmlspecialchars($value)
);
}

/** Instagram URL **/
elseif ($field->id == 33){
echo sprintf('<a href="%s"%s> <img alt="Follow us on Instagram" src="data:image/svg+xml;base64," style="width:32px;height:auto;"></a>',
	htmlspecialchars($value),
	$attributes,
	htmlspecialchars($value)
);
  
} else {
echo sprintf('<a href="%s"%s>%s</a>',
	htmlspecialchars($value),
	$attributes,
	htmlspecialchars($value)
);
}